<?php
if ( ! class_exists( 'HHTheme' ) ) {

	/**
	 * Class HHTheme
	 */
	class HHTheme {


		/**
		 * HHTheme constructor.
		 */
		public function __construct() {

			define( 'THEME_PATH', get_template_directory() );
			define( 'THEME_INC_PATH', get_template_directory() . '/inc/' );
			define( 'THEME_INC_URL', get_template_directory_uri() . '/inc/' );
			define( 'THEME_ASSETS_SRC', get_stylesheet_directory_uri() . '/assets/src/' );
			define( 'THEME_ASSETS_DIST', get_stylesheet_directory_uri() . '/assets/dist/' );

			/**
			 * ACTIONS
			 */
			add_action( 'wp_enqueue_scripts', array( $this, 'enqueue_front' ) );
			add_action( 'admin_menu', array( $this, 'register_hh_page' ) );
			add_action( 'init', array( $this, 'register_post_type' ) );
			add_action( 'init', array( $this, 'register_taxonomies' ) );
			add_action( 'acf/init', array( $this, 'register_acf_blocks' ) );

			/**
			 * THEME SUPPORT
			 */
			add_action( 'after_setup_theme', array( $this, 'theme_support' ) );
			add_action( 'init', array( $this, 'register_hh_option_page' ) );
			add_action( 'acf/init', array( $this, 'register_acf_blocks' ) );

			remove_action( 'wp_body_open', 'wp_global_styles_render_svg_filters' );

			add_action( 'login_head', array( $this, 'hh_custom_logo' ) );

		}

		/**
		 * Register Post Type
		 */
		public function register_post_type() {
			$post_types = array(
				array(
					'name'   => 'solution',
					'tax'    => array( 'public' ),
					'icon'   => 'dashicons-editor-ul',
					'slug'   => get_field( 'translate_solutions_slug', 'option' ),
					'labels' => array(
						'name'          => __( 'Solutions', 'hulahoop' ),
						'singular_name' => __( 'Solution', 'hulahoop' ),
						'add_new'       => __( 'Ajouter une solution', 'hulahoop' ),
						'not_found'     => __( 'Aucune solution trouvée', 'hulahoop' ),
					),
				),
			);
			foreach ( $post_types as $cpt ) :
				$post_type_name   = acf_maybe_get( $cpt, 'name' );
				$post_type_tax    = acf_maybe_get( $cpt, 'tax' );
				$post_type_icon   = acf_maybe_get( $cpt, 'icon' );
				$post_type_slug   = acf_maybe_get( $cpt, 'slug' );
				$post_type_labels = acf_maybe_get( $cpt, 'labels' );
				$post_type_args   = array(
					'labels'             => $post_type_labels,
					'has_archive'        => true,
					'show_in_nav_menus'  => true,
					'show_ui'            => true,
					'publicy_queriable'  => true,
					'show_in_rest'       => true,
					'public'             => true,
					'show_in_quick_edit' => true,
					'meta_box_cb'        => true,
					'hierarchical'       => true,
					'query_var'          => true,
					'taxonomies'         => $post_type_tax,
					'rewrite'            => array(
						'slug'       => $post_type_slug,
						'with_front' => true,
					),
					'menu_icon'          => $post_type_icon,
					'supports'           => array(
						'excerpt',
						'title',
						'thumbnail',
						'custom-fields',
						'editor',
						'revisions',
					),
				);
				register_post_type( $post_type_name, $post_type_args );
			endforeach;
		}

		/**
		 * Register Taxonomies
		 */
		public function register_taxonomies() {
			$taxonomies = array(
				array(
					'name'      => 'public',
					'post_type' => array( 'solution' ),
					'labels'    => array(
						'name'          => __( 'Type de public', 'hulahoop' ),
						'singular_name' => __( 'Public', 'hulahoop' ),
						'add_new'       => __( 'Ajouter un type de public', 'hulahoop' ),
						'add_new_item'  => __( 'Ajouter un type de public', 'hulahoop' ),
						'not_found'     => __( 'Aucun type de public trouvé', 'hulahoop' ),
					),
				),
			);
			foreach ( $taxonomies as $current_taxonomy ) :
				$tax_name      = acf_maybe_get( $current_taxonomy, 'name' );
				$tax_labels    = acf_maybe_get( $current_taxonomy, 'labels' );
				$tax_post_type = acf_maybe_get( $current_taxonomy, 'post_type' );
				$args          = array(
					'show_in_rest' => true,
					'hierarchical' => true,
					'labels'       => $tax_labels,
				);
				register_taxonomy( $tax_name, $tax_post_type, $args );
			endforeach;
		}

		/**
		 * Custom Logo login page
		 */
		public function hh_custom_logo() {
			$logo_url = wp_get_attachment_url( get_theme_mod( 'custom_logo' ) );
			if ( ! empty( $logo_url ) ) {
				echo '<style type="text/css">h1 a { background-image:url(' . esc_url( $logo_url ) . ') !important;height:100px!important;width:100px!important; background-size:100% !important;line-height:inherit !important;}</style>';

			}
		}

		/**
		 * Register blocks
		 */
		public function register_acf_blocks() {
			$blocks = array(
				array(
					'name'           => 'rich-content',
					'title'          => 'Contenu riche',
					'enqueue_style'  => true,
					'enqueue_script' => false,
				),
			);

			foreach ( $blocks as $block ) {
				$args = array(
					'name'            => acf_maybe_get( $block, 'name' ),
					'title'           => acf_maybe_get( $block, 'title' ),
					'align'           => 'full',
					'mode'            => 'edit',
					'enqueue_style'   => acf_maybe_get( $block, 'enqueue_style' ) ? THEME_BLOCKS_URL . acf_maybe_get( $block, 'name' ) . '/' . acf_maybe_get( $block, 'name' ) . '.min.css' : '',
					'enqueue_script'  => acf_maybe_get( $block, 'enqueue_script' ) ? THEME_BLOCKS_URL . acf_maybe_get( $block, 'name' ) . '/' . acf_maybe_get( $block, 'name' ) . '.prod.js' : '',
					'example'         => array(
						'attributes' => array(
							'mode' => 'preview',
							'data' => array(
								'preview_image_help' => 'template-parts/blocks/' . acf_maybe_get( $block, 'name' ) . '/' . acf_maybe_get( $block, 'name' ) . '.png',
							),
						),
					),
					'render_template' => 'template-parts/blocks/' . acf_maybe_get( $block, 'name' ) . '/' . acf_maybe_get( $block, 'name' ) . '.php',
					'icon'            => '<svg xmlns=\'http://www.w3.org/2000/svg\' xmlns:xlink=\'http://www.w3.org/1999/xlink\' width=\'36px\' height=\'36px\' viewBox=\'0 0 36 36\' version=\'1.1\'><title>Artboard</title><g id=\'Artboard\' stroke=\'none\' stroke-width=\'1\' fill=\'none\' fill-rule=\'evenodd\'><image id=\'hulahoop-logo\' x=\'1.80487805\' y=\'0.842553191\' width=\'32.4796748\' height=\'34\' xlink:href=\'data:image/png;base64,\'/></g></svg>',
				);
				acf_register_block_type( $args );
			}
		}

		/**
		 * Theme support
		 */
		public function theme_support() {
			add_theme_support( 'align-wide' );
			add_theme_support( 'custom-logo' );
			add_theme_support( 'title-tag' );
			add_theme_support( 'post-thumbnails' );
			add_theme_support( 'menus' );
		}

		/**
		 * Enqueue styles and scripts
		 */
		public function enqueue_front() {
			wp_enqueue_style( 'main-css', THEME_ASSETS_DIST . 'main.min.css', array(), '1.0', 'all' );
			wp_enqueue_style( 'custom-css', THEME_ASSETS_DIST . 'custom.min.css', array(), '1.0', 'all' );
		}

		/**
		 * Register HulaHoop Page
		 */
		public function register_hh_page() {
			add_menu_page(
				__( 'HulaHoop', 'textdomain' ),
				'HulaHoop',
				'manage_options',
				'hulahoop/hulahoop-admin.php',
				'',
				'data:image/svg+xml;base64,',
				999
			);
		}

		/*
		* Page option
		*/
		public function register_hh_option_page() {
			if ( function_exists( 'acf_add_options_page' ) ) {
				acf_add_options_page(
					array(
						'page_title'  => 'Configuration',
						'menu_title'  => 'Configuration',
						'parent_slug' => 'hulahoop/hulahoop-admin.php',
						'redirect'    => false,
					)
				);
			}
		}
	}

	if ( function_exists( 'acf_new_instance' ) ) {
		acf_new_instance( 'HHTheme' );
		include_once THEME_INC_PATH . 'post-types.php';
		include_once THEME_INC_PATH . 'hh-tracking.php';
		include_once THEME_INC_PATH . 'taxonomies.php';
		include_once THEME_INC_PATH . 'field-groups.php';
	}
}
